import React, { useState } from 'react';

function ReservaForm({ servicios, onReserva }) {
  const [formData, setFormData] = useState({
    servicioId: '',
    fechaInicio: '',
    fechaFin: '',
    nombreCliente: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.servicioId || !formData.fechaInicio || !formData.fechaFin || !formData.nombreCliente) {
      alert('Por favor, complete todos los campos.');
      return;
    }
    
    const reserva = {
      ...formData,
      fechaInicio: new Date(formData.fechaInicio),
      fechaFin: new Date(formData.fechaFin)
    };

    onReserva(reserva);
    setFormData({ servicioId: '', fechaInicio: '', fechaFin: '', nombreCliente: '' });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Hacer una Reserva</h2>
      <select 
        value={formData.servicioId} 
        onChange={(e) => setFormData({ ...formData, servicioId: e.target.value })}
      >
        <option value="">Seleccione un servicio</option>
        {servicios.map(servicio => (
          <option key={servicio.id} value={servicio.id}>{servicio.nombre}</option>
        ))}
      </select>
      <input 
        type="datetime-local" 
        value={formData.fechaInicio} 
        onChange={(e) => setFormData({ ...formData, fechaInicio: e.target.value })}
      />
      <input 
        type="datetime-local" 
        value={formData.fechaFin} 
        onChange={(e) => setFormData({ ...formData, fechaFin: e.target.value })}
      />
      <input 
        type="text" 
        value={formData.nombreCliente} 
        onChange={(e) => setFormData({ ...formData, nombreCliente: e.target.value })}
        placeholder="Nombre del cliente"
      />
      <button type="submit">Reservar</button>
    </form>
  );
}

export default ReservaForm;
